declare module '*.svg' {
  const content: React.FC<React.SVGAttributes<SVGElement>>;
  export default content;
}

declare module '*.woff2' {
  const content: any;
  export default content;
}

declare module '*.woff' {
  const content: any;
  export default content;
}

declare module '*.ttf' {
  const content: any;
  export default content;
}
